				$count = 0;
			} else {

				$criteria->add(QuizusrPeer::ID_QUESTION, $this->id_question);

				$count = QuizusrPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(QuizusrPeer::ID_QUESTION, $this->id_question);

				if (!isset($this->lastQuizusrCriteria) || !$this->lastQuizusrCriteria->equals($criteria)) {
					$count = QuizusrPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collQuizusrs);
