	/**
	 * Method called to associate a Quizusr object to this object
	 * through the Quizusr foreign key attribute.
	 *
	 * @param      Quizusr $l Quizusr
	 * @return     void
	 * @throws     PropelException
	 */
	public function addQuizusr(Quizusr $l)
	{
		if ($this->collQuizusrs === null) {
			$this->initQuizusrs();
		}
		if (!in_array($l, $this->collQuizusrs, true)) { // only add it if the **same** object is not already associated
			array_push($this->collQuizusrs, $l);
			$l->setQuestion($this);
		}
	}


